import React, { useState } from 'react';
import { Search, Compass, Map, Award } from 'lucide-react';
import Navbar from './components/Navbar';
import PreferenceCard from './components/PreferenceCard';
import TripCard from './components/TripCard';
import TripsView from './views/TripsView';
import RewardsView from './views/RewardsView';
import Login from './components/Login';
import Register from './components/Register';
import UserProfile from './components/UserProfile';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentView, setCurrentView] = useState('explore');
  const [view, setView] = useState('login'); // 'login', 'register', 'main', o 'profile'

  const handleLogin = (username) => {
    setIsLoggedIn(true);
    setView('main');
    alert(`Bienvenido ${username}`);
  };

  const handleRegister = () => {
    setView('register');
  };

  const handleRegisterComplete = () => {
    setView('login');
  };

  const handleProfileClick = () => {
    setView('profile');
  };

  const handleProfileClose = () => {
    setView('main');
  };

  const renderView = () => {
    switch (currentView) {
      case 'trips':
        return <TripsView />;
      case 'rewards':
        return <RewardsView />;
      default:
        return (
          <>
            {/* Barra de b√∫squeda */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400" />
              <input
                type="search"
                placeholder="Buscar destinos en Colombia..."
                className="w-full pl-10 pr-4 py-3 bg-white rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Preferencias actuales */}
            <section>
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Tus Preferencias</h2>
              <PreferenceCard />
            </section>

            {/* Viajes destacados */}
            <section>
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-xl font-semibold text-gray-900">Destinos Destacados</h2>
                <button
                  onClick={() => setCurrentView('trips')}
                  className="text-blue-600 font-medium"
                >
                  Ver Todos
                </button>
              </div>
              <div className="space-y-4">
                <TripCard
                  imageUrl="data:image/jpeg;base64,"
                  title="Aventura en Tayrona"
                  rating={4.9}
                  timeLeft="24 horas"
                  location="Santa Marta"
                />
                <TripCard
                  imageUrl="data:image/jpeg;base64,"
                  title="M√°gica Cartagena"
                  rating={4.8}
                  timeLeft="48 horas"
                  location="Cartagena"
                />
              </div>
            </section>
          </>
        );
    }
  };

  if (view === 'login') {
    return (
      <Login
        onLogin={handleLogin}
        onRegister={handleRegister}
      />
    );
  }

  if (view === 'register') {
    return (
      <Register
        onRegister={handleRegisterComplete}
        onGoToLogin={() => setView('login')}
      />
    );
  }

  if (view === 'profile') {
    return (
      <UserProfile
        username="usuarioEjemplo"
        email="usuario@ejemplo.com"
        onEditProfile={() => alert('Editar perfil')}
        onCloseProfile={handleProfileClose} // Llama a la funci√≥n para cerrar el perfil
      />
    );
  }
  

  return (
    <div className="min-h-screen bg-gray-50 pb-20">
      <Navbar onProfileClick={handleProfileClick} />

      <main className="pt-20 px-4 space-y-6">
        {renderView()}
      </main>

      {/* Navegaci√≥n inferior */}
      <div className="fixed bottom-0 left-0 right-0 bg-white border-t">
        <div className="flex justify-around py-3">
          {[
            { icon: Compass, label: 'Explorar', value: 'explore' },
            { icon: Map, label: 'Viajes', value: 'trips' },
            { icon: Award, label: 'Premios', value: 'rewards' },
          ].map((item) => (
            <button
              key={item.value}
              onClick={() => setCurrentView(item.value)}
              className="flex flex-col items-center space-y-1"
            >
              <item.icon
                className={`h-6 w-6 ${
                  currentView === item.value ? 'text-blue-600' : 'text-gray-400'
                }`}
              />
              <span
                className={`text-xs ${
                  currentView === item.value ? 'text-blue-600' : 'text-gray-500'
                }`}
              >
                {item.label}
              </span>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
