export default function TripsView() {
  const [reservations, setReservations] = useState(Array(trips.length).fill(false));
  const [selectedTrip, setSelectedTrip] = useState(null);

  const handleReservation = (index) => {
    setReservations((prevReservations) => {
      const newReservations = [...prevReservations];
      newReservations[index] = true;
      return newReservations;
    });
  };

  const handleImageClick = (trip) => {
    setSelectedTrip(trip);
  };

  const handleCloseModal = () => {
    setSelectedTrip(null);
  };

  return (
    <div className="">
      <div className="flex items-center justify-center mb-4 bg-gradient-to-br ">
        <div className="w-full max-w-md p-10 space-y-8 bg-blue-600 rounded-2xl shadow-2xl">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-white">Viajes Sorpresa con Inteligencia Artificial</h2>
            <p className="mt-4 text-zinc-200">
              Con nuestra avanzada inteligencia artificial, analizaremos tus preferencias para ofrecerte
              recomendaciones personalizadas de destinos, asegurando una experiencia única adaptada a tus gustos
              y necesidades.
            </p>
          </div>
        </div>
      </div>
      <div className="flex items-center justify-center mb-4 bg-gradient-to-br ">
        <h1 className="text-2xl mt-2 font-bold text-gray-900">Destinos en Colombia</h1>
      </div>

      <div className="space-y-4">
        {trips.map((trip, index) => (
          <div key={index} className="bg-white p-6 rounded-lg shadow-md">
            <img 
              src={trip.imageUrl} 
              alt={trip.title} 
              className="w-full h-40 object-cover rounded-md mb-4 cursor-pointer"
              onClick={() => handleImageClick(trip)}
            />
            <h3 className="text-xl font-semibold mb-2">{trip.title}</h3>
            <div className="flex items-center mb-2">
              {[...Array(5)].map((_, i) => (
                <FaStar key={i} color={i < Math.round(trip.rating) ? "#ffc107" : "#e4e5e9"} />
              ))}
              <span className="ml-2 text-gray-600">{trip.rating}</span>
            </div>
            <p className="text-gray-700 mb-2">Ubicación: {trip.location}</p>
            <p className="text-gray-700 mb-4">Duración: {trip.timeLeft}</p>
            <button
              onClick={() => handleReservation(index)}
              className={`px-4 py-2 rounded-lg text-white ${reservations[index] ? 'bg-green-600' : 'bg-blue-600 hover:bg-blue-700'}`}
            >
              {reservations[index] ? 'Reservado' : 'Realizar Reserva'}
            </button>
          </div>
        ))}
      </div>

      {selectedTrip && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-lg w-full">
          
            <h3 className="text-2xl font-semibold mb-4">{selectedTrip.title}</h3>
            <img src={selectedTrip.imageUrl}/>
            <p className="text-gray-700 mb-4">{selectedTrip.description}</p>
            <p className="text-gray-700 mb-2">Ubicación: {selectedTrip.location}</p>
            <p className="text-gray-700 mb-4">Duración: {selectedTrip.timeLeft}</p>
            <button
              onClick={handleCloseModal}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              Cerrar
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
